INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (29, '中国文化概论', 'literature_social', 'books/WE.jpg', 15.00, 0, '线下当面交付', '本书概论中国文化，在编章展开之前，有必要就全书的核心概念、涉及范围、基本宗旨等问题预作说明，是为绪论。\r\n本书作为高等学校人文素质教育公共课教材，意在给大学生们提供一个了解祖国悠久、丰富文化遗产的简明文本，使之对于中国文化的特征有所把握，并对中国文化的继承和创新问题有所思考。我们不企求读者同意本书的全部特点，但望能启发朝气蓬勃的探索。', 7);
