INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (23, '家庭教師HITMAN REBORN!2', 'other', 'books/家教2.jpg', 35.00, 0, '线上付款，顺丰/圆通配送', '第二册~阿綱的面前出現一位妖艷的美女…不過她是毒蠍子‧碧洋琪這位殺手！碧洋琪希望心愛的里包恩能夠重回殺手的行列，於是想要除掉礙眼的阿綱…！？雖然危險卻很愉快的傢伙將在第２集陸續登場喔！', 3);
