INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (21, '数据库系统概论', 'science_engineering', 'books/DB.jpg', 20.00, 0, '线下当面交付', '《数据库系统概论（第5版）“十二五”普通高等教育本科国家级规划教材》系统全面地阐述了数据库系统的基础理论、基本技术和基本方法。全书分为4篇16章。第一篇基础篇，包括绪论、关系数据库、关系数据库标准语言SQL、数据库安全性和数据库完整性，共5章', 2);
