INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (19, '黄雀记', 'other', 'books/黄雀记.jpg', 15.00, 0, '若在NKU，亲手送达宿舍楼，或线上付款，顺丰/圆通配送', '每个人不动声色的日常生活中都隐藏着肮 脏甚至罪 恶的因子。保润、柳生、仙女——不，白小姐——的生活也不外如此。他们是从香椿树成长起来的，他们的血液里流淌着原罪。这种原罪，不是小说中庞太太那苍白而虚伪的《圣经》和上帝能解释的，而切切实实是随时间推移而越发膨胀的罪恶。', 1);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (18, '中国文化要略（第4版）', 'literature_social', 'books/z.jpg', 20.00, 0, '若在NKU，亲手送达宿舍楼，或线上付款，顺丰/圆通配送', '本书以简洁的笔触、生动的语言，勾画出中国文化的历史渊源和发展脉络。全书分为“简论”“地理概况”“历史纵横”“姓氏与名、字、号”“汉字”“学术思想”“宗教信仰”“古代教育”“科举制度”“典籍藏书”“科技成就”“传统建筑”“古典文学”“各类艺术”“精美器物”“风俗习惯”“中外文化交流”等17章。', 5);
