INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (15, '细胞生物学', 'biology_medicine', 'books/生物.jpg', 15.00, 0, '线上付款，顺丰/圆通配送', '有意思的一本书（虽然闭书默思后，觉得此书每个标点符号都是知识点，但是一个没记住）。泰勒斯在2000多年前如果有当今如此微观之技术，所带来的视野，估计也会失去仰望天空而哲思的兴趣，而转向探索一番我们自身的细胞世界。毕竟我们自身才是一切知识的本源啊。', 3);
