INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (12, '曼昆宏观经济学原理', 'financial_business', 'books/宏观.jpg', 40.00, 0, '线下当面交付', '《经济学原理》主要介绍了经济学十大原理，这十大原理贯穿于书中始末，并运用它们对贸易，供求关系，销量与生产，企业行为和行业组织，劳动力市场以及宏观经济学理论进行阐述。该书分微观经济学和宏观经济学上、下两分册，上册为《微观经济学分册》，下册为《宏观经济学分册》', 5);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (11, '曼昆微观经济学原理', 'financial_business', 'books/微观.jpg', 50.01, 0, '线下当面交付', '《经济学原理》主要介绍了经济学十大原理，这十大原理贯穿于书中始末，并运用它们对贸易，供求关系，销量与生产，企业行为和行业组织，劳动力市场以及宏观经济学理论进行阐述。该书分微观经济学和宏观经济学上、下两分册，上册为《微观经济学分册》，下册为《宏观经济学分册》', 5);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (10, '软件安全', 'science_engineering', 'books/软件安全.jpg', 30.00, 0, '线下当面交付', '本书全面介绍汇编语言和逆向分析基础知识、软件漏洞的利用及挖掘、面向 Web 应用的渗透测试、CTF 题型及演示，配合丰富的实践案例(视频教程、慕课资源)，是一本全面、基础、专业的入门级教程。', 7);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (9, '我的帝王生涯', 'other', 'books/帝王.png', 20.00, 0, '线下当面交付', '“历史”的本来意义已经消失，它可以是作家艺术思维的构架，也可以是小说中人物生存境遇的象征。苏童在小说中看重的是历史与帝王文化这种极端形式对人性的追问与逼视，探讨在这种特定情境中角色如何发生变化，人性又是如何丧失、救赎与失败，他在意的是“人性”的故事和空间，而不是“历史”，于是小说对社会、历史进行有意忽略，以近乎梦呓的方式表达人性与人生。', 3);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (8, '白银似雪', 'art_music', 'books/白银.png', 50.00, 0, '线上付款，顺丰/圆通配送', '东方画姬德珍力作！璀璨洁白似雪的白银，如柔情似水的苗疆女子；朦朦的烟雨，如云雾缥缈，多情的苗女倚盼着心上人的到来。', 1);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (7, '声乐基础教学法:演唱的基础和过程', 'art_music', 'books/声乐_Xfb8L7u.png', 30.00, 0, '线上付款，顺丰/圆通配送', '《当代外国高校精品音乐教材·声乐基础教学法：演唱的基础和过程》是一本全面介绍歌唱与教学的声乐教学法著作，适合作为大学的教科书。虽然此书主要是为那些从事或将要从事声乐教学的教师而作，但是，它也适用于合唱指挥、声乐指导及歌唱水平处于中高级程度的歌唱者。读者会发现此书清晰易懂，是一本很好的训练手册及参考用书。', 7);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (6, '当我们谈论爱情时我们在谈论什么', 'other', 'books/卡佛.jpg', 25.00, 0, '线上付款，顺丰/圆通配送', '《当我们谈论爱情时我们在谈论什么》出版于1981年，是雷蒙德•卡佛的成名之作，同时也是其最负盛名的代表作。本书由十七篇短篇小说组成，讲述了如餐馆女招待、锯木厂工人、修车工、推销员和汽车旅馆管理员等社会底层的体力劳动者的生活。这些普通人有着普通人的愿望，做着再普通不过的事情，但他们发现自己在为生存而挣扎，无法获得在常人看来并不远大的人生目标。他们的生活中充满了窘困和不如意，婚姻破裂，失业，酗酒，破产。卡佛用“极简”的遣词、冷静疏离的叙事，表现了现代社会中人的边缘性以及现代人脆弱的自我意识。', 3);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (5, '中国当代文学作品选 第一册', 'literature_social', 'books/文一.png', 18.00, 0, '线下当面交付', '精选了新中国五十年的小说、散文、诗歌、剧本、报告文学等文学佳作，所选作品异彩纷呈，可读可诵读，读者可轻松把握新中国五十余年文学全貌。文学大家的杰作、艺苑新秀的佳作、各流派的代表作、各时期的重要之作，本书均有收录，它们表现了新中国文学五十年的流变。', 6);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (4, '21世纪保险精算', 'financial_business', 'books/21.jpg', 18.00, 0, '线下当面交付', '本教材系统介绍精算学的基础知识，基本技能和基本方法、全书分为寿险精算和非寿险精算两部分。', 5);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (3, '生理学', 'biology_medicine', 'books/生理学.jpg', 20.00, 0, '线上付款，顺丰/圆通配送', '生理书，读一遍觉得东西好多！读第二遍，就看出布局来了，无非是循环系统、神经系统、泌尿系统、消化系统、呼吸系统、内分泌系统的那些事儿！读第三遍，又有了一种整体观，人是这样一个严谨精密的系统，人脑相当于一盏25W的灯，运筹帷幄，控制一切活动、思想与欲望。整个人体，表面上饱满丰腴，实则内部暗流涌动……我觉得，我们人体就是一部摆在那里的哲学书，是造物智慧的结晶。', 4);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (2, '计算机组成与设计 硬件/软件接口原书第6版', 'science_engineering', 'books/计组.png', 35.00, 0, '线上付款，顺丰/圆通配送', '图灵奖得主联袂撰写，计算机体系结构新黄金时代必读之作，每章都新增了关于领域专用体系结构的讨论。由2017年图灵奖的两位得主撰写，是计算机体系结构领域的经典教材，每年被超过40000名学生使用。新版的主要更新是在每章中添加了关于DSA的内容，并更新了所有实例，使全书更加与时俱进，满足学生和读者的新需求。很好的教材使我脑袋通畅^_^', 1);
INSERT INTO `books_booksinfo` (`id`, `title`, `type`, `picture`, `price`, `isDelete`, `address`, `description`, `user_id`) VALUES (1, '汉妆潋滟', 'art_music', 'books/汉妆潋滟.jpg', 100.00, 0, '线下当面交付', '東方畫姬德珍力作！几乎全新。漫畫金像獎網路票選超人氣女主角獎·最佳圖文本獎！繼05《清風拂面》、06《白銀似雪》-2007年度距獻——漢家兒女譜出的亙古亙今綿綿情話 細述漢族女子流離命運捕捉歷史洪流中顛沛情境~', 1);
